import React, { Component } from "react";
import { historyWrapper } from "../components/HistoryWrapper";
import { authWrapper } from "./AuthWrapper";
import ValidatedForm from "../forms/ValidatedForm";

export const AuthPrompt = historyWrapper(
  authWrapper(
    class extends Component {
      constructor(props) {
        super(props);
        this.state = {
          errorMessage: null,
        };
        this.defaultAttrs = { required: true };
        this.formModel = [
          { label: "Username", attrs: { defaultValue: "admin" } },
          { label: "Password", attrs: { defaultValue: "password" } },
        ];
      }

      authenticate = (credentials) => {
        this.props
          .authenticate(credentials)
          .catch((err) => this.setState({ errorMessage: err.message }))
          .then(this.props.history.push("/admin"));
      };

      render = () => (
        <div className="container-fluid">
          <div className="row">
            <div className="col bg-dark text-white">
              <div className="navbar-brand">SPORTS STORE</div>
            </div>
          </div>
          <div className="row">
            <div className="col m-2">
              {this.state.errorMessage != null && (
                <h4 className="bg-danger text-center text-white m-1 p-2">
                  {this.state.errorMessage}
                </h4>
              )}
              <ValidatedForm
                formModel={this.formModel}
                defaultAttrs={this.defaultAttrs}
                submitCallback={this.authenticate}
                submitText="Login"
                cancelCallback={() => this.props.history.push("/")}
                cancelText="Cancel"
              />
            </div>
          </div>
        </div>
      );
    }
  )
);
